package homework15;

public class Review02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		Write a program to print numbers 
//		from 10 to 3 on the console 
//		by using do-while loop
		
		int i = 10;
		do{
			System.out.print(i + " ");
			i--;
			
		}while((i>=3));
	}

}
